    class GlobalUniformMappings
    {
        public class Mapping
        {
            public readonly ShaderParameter.RType ExpectedType;
            public string Name, TemplateName;
            public int RegisterIndex, ArgumentIndex;
            public DrawModeRegisterOffsetType OffsetType;
            public ShaderMode shaderMode;

            public Mapping(string name, string template_name, ShaderParameter.RType type, int register_index, int argument_index, DrawModeRegisterOffsetType offset_type, ShaderMode shadermode)
            {
                Name = name;
                TemplateName = template_name;
                ExpectedType = type;
                RegisterIndex = register_index;
                ArgumentIndex = argument_index;
                OffsetType = offset_type;
                shaderMode = shadermode;

                switch (offset_type)
                {
                    case DrawModeRegisterOffsetType.ArgumentsVectorRegisters:
                    case DrawModeRegisterOffsetType.RenderBufferSamplerRegisters:
                    case DrawModeRegisterOffsetType.GlobalArgumentsVectorRegisters:

                        ArgumentIndex = -1;
                        if (RegisterIndex >= 58)
                            RegisterIndex = -1;

                        break;
                    case DrawModeRegisterOffsetType.Unknown3:

                        RegisterIndex = -1;

                        break;
                    default:

                        if (RegisterIndex >= 58)
                            RegisterIndex = -1;

                        break;
                }


            }
        }

        static Mapping[] CombineMappings()
        {
            List<Mapping> mappings = new List<Mapping>();

            mappings.AddRange(UnknownBlock);

            return mappings.ToArray();
        }

        public static Mapping GetMapping(string name, string template_name, RType register_type, ShaderMode mode)
        {
            string _name = name.ToLower();

            var query = MappingsSource.Where(
                mapping => mapping.Name == _name &&
                mapping.ExpectedType == register_type &&
                mapping.shaderMode == mode &&
                mapping.TemplateName == template_name).ToArray();

            if (query.Count() > 1) throw new Exception("Invalid data/query");
            return query.Count() > 0 ? query.ElementAt(0) : null;
        }

